import React, { useCallback, useEffect, useRef, useState } from 'react';
import Todos from './components/Todos';
import CreateTodo from './components/CreateTodo';

import { todos as todosData } from './data/todos_data';

import './css/App.css';

const App = () => {
	const [todos, setTodos] = useState([]);

	let waitings = useRef([]);
	let inprogress = useRef([]);
	let completeds = useRef([]);

	useEffect(() => {
		const todos = todosData;
		// const todos = JSON.parse(localStorage.getItem('todos')) || todosData;
		setTodos(todos);
		return () => localStorage.setItem('todos', JSON.stringify(todos));
	}, [])

	const addTodo = useCallback((todo) => {
		setTodos([todo, ...todos]);
	}, [todos]);

	const updateTodo = (ntodos) => {
		setTodos(ntodos);
	};

	useEffect(() => {

		waitings.current = todos.filter(todo => todo.status === 'waiting');
		inprogress.current = todos.filter(todo => todo.status === 'inprogress');
		completeds.current = todos.filter(todo => todo.status === 'completed');
	}, [todos])

	return (
		<div className='App'>
			<Todos todos={waitings.current} updateTodo={updateTodo} />
			<Todos todos={inprogress.current} updateTodo={updateTodo} />
			<Todos todos={completeds.current} updateTodo={updateTodo} />
			<CreateTodo addTodo={addTodo} />
		</div>
	);
}

export default App;
