import React, { useState } from 'react';

class Todo {
	constructor(title, body) {
		this.title = title;
		this.body = body;
		this.completed = false;
		this.started = false;
		this.inprogress = false;
	}
}

const newTodo = new Todo('homework', 'I have to do the homework just in time');


const CreateTodo = () => {
	const [state, setState] = useState({ title: '', body: '' });

	const changeHandler = (event) => {
		setState({ ...state, [event.target.name]: event.target.value });
	};
	const submitHandler = (event) => {
		event.preventDefault();
		const newTodo = new Todo(state.title, state.body);
		console.log(newTodo);
	};
	return (
		<div>
			<input
				className='input'
				id='title'
				type='text'
				name='title'
				value={state.title}
				onChange={changeHandler}
				placeholder='The title here'
				required
			/>
			<textarea
				className='input'
				id='body'
				type='text'
				name='body'
				rows={5}
				value={state.body}
				onChange={changeHandler}
				placeholder='The description goes here'
				required
			/>
			<input
				className='input btn'
				id='title'
				type='button'
				value='Create a Todo'
				onChange={changeHandler}
				onClick={submitHandler}
			/>
		</div>
	);
};

export default CreateTodo;
