import React from 'react';
const todos = [
	{
		title: 'the title 1',
		body: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Nemo voluptate odit quos, iure in impedit harum odio sint, molestiae nihil error reprehenderit praesentium deserunt ea possimus? Culpa dolores voluptatibus sequi!',
		status: 'waiting',
	},
	{
		title: 'the title 2',
		body: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Nemo voluptate odit quos, iure in impedit harum odio sint.',
		status: 'completed',
	},
	{
		title: 'the title 3',
		body: ', molestiae nihil error reprehenderit praesentium deserunt ea possimus? Culpa dolores voluptatibus sequi!',
		status: 'inprogress',
	},
];
const Todos = () => {

	const changeHandler = (event) => {
		//should update
	};
	return (
		<div>
			{
				todos.map((todo, index) => {
					return (
						<div key={index}>
							<dl>
								<dt>{todo.title}</dt>
								<dd>{todo.body}</dd>
							</dl>
							<div>
								<div>
									<input
										id='waiting'
										type='radio'
										name={todo.title}
										value='waiting'
										checked={todo.status === 'waiting'}
									// onChange={changeHandler}
									/>
									<label htmlFor='waiting'>waiting</label>
								</div>
								<div>
									<input
										id='inprogress'
										type='radio'
										name={todo.title}
										value='inprogress'
										checked={todo.status === 'inprogress'}
									// onChange={changeHandler}
									/>
									<label htmlFor='inprogress'>inprogress</label>
								</div>
								<div>
									<input
										id='completed'
										type='radio'
										name={todo.title}
										value='completed'
										checked={todo.status === 'completed'}
									// onChange={changeHandler}
									/>
									<label htmlFor='completed'>completed</label>
								</div>
							</div>
						</div>
					);
				})
			}
		</div>
	);
};

export default Todos;
