import React from 'react';
const AlertMessage = ({ message }) => {
	const style = {
		display: 'absolute',
		top: '-1em',
		left: '1em',
		width: 'fit-content',
		padding: '2px 5px',
		fontSize: '0.7rem',
		// height: '20px',
		border: '1px solid red',
		borderRadius: '4px',
		backgroundColor: 'white',
		color: 'red',
	};
	return (
		<div style={style} >
			{message}
		</div>
	);
};

export default AlertMessage;
