import '../css/Todos.css';
const todos = [
	{
		title: 'the title 1',
		body: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Nemo voluptate odit quos, iure in impedit harum odio sint, molestiae nihil error reprehenderit praesentium deserunt ea possimus? Culpa dolores voluptatibus sequi!',
		status: 'waiting',
	},
	{
		title: 'the title 2',
		body: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Nemo voluptate odit quos, iure in impedit harum odio sint.',
		status: 'completed',
	},
	{
		title: 'the title 3',
		body: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Nemo voluptate odit quos, iure in impedit harum odio sint.',
		status: 'completed',
	},
	{
		title: 'the title 4',
		body: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Nemo voluptate odit quos, iure in impedit harum odio sint, molestiae nihil error reprehenderit praesentium deserunt ea possimus? Culpa dolores voluptatibus sequi!',
		status: 'waiting',
	},
	{
		title: 'the title 5',
		body: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Nemo voluptate odit quos, iure in impedit harum odio sint.',
		status: 'completed',
	},
	{
		title: 'the title 6',
		body: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Nemo voluptate odit quos, iure in impedit harum odio sint.',
		status: 'completed',
	},
	{
		title: 'the title 7',
		body: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Nemo voluptate odit quos, iure in impedit harum odio sint.',
		status: 'completed',
	},
	{
		title: 'the title 8',
		body: ', molestiae nihil error reprehenderit praesentium deserunt ea possimus? Culpa dolores voluptatibus sequi!',
		status: 'inprogress',
	},
];
const Todos = () => {

	const changeHandler = (event) => {
		//should update
	};
	return (
		<div className='Todos'>
			{
				todos.map((todo, index) => {
					return (
						<div key={index} className='todo'>
							<dl className='todo__header--con'>
								<dt className='todo__title'>{todo.title}</dt>
								<dd className='todo__body'>{todo.body}</dd>
							</dl>
							<div className='todo__status--con'>
								<div className='todo__status'>
									<input
										className='todo__status--item'
										id='waiting'
										type='radio'
										name={todo.title}
										value='waiting'
										checked={todo.status === 'waiting'}
									// onChange={changeHandler}
									/>
									<label className='todo__status--item' htmlFor='waiting' >waiting</label>
								</div>
								<div className='todo__status'>
									<input
										className='todo__status--item'
										id='inprogress'
										type='radio'
										name={todo.title}
										value='inprogress'
										checked={todo.status === 'inprogress'}
									// onChange={changeHandler}
									/>
									<label className='todo__status--item' htmlFor='inprogress'>inprogress</label>
								</div >
								<div className='todo__status'>
									<input
										className='todo__status--item'
										id='completed'
										type='radio'
										name={todo.title}
										value='completed'
										checked={todo.status === 'completed'}
									// onChange={changeHandler}
									/>
									<label className='todo__status--item' htmlFor='completed'>completed</label>
								</div>
							</div>
						</div>
					);
				})
			}
		</div>
	);
};

export default Todos;
