import React, { useRef, useState } from 'react'
const NAME_REGEX = /^[a-z]|[_@#$&][0-9]{4}/;
console.log(NAME_REGEX.test('0'))
const Register = () => {
	const usernameRef = useRef(null);
	const pwdRef = useRef(null);
	const cfrmPwdRef = useRef(null);
	const btnRef = useRef(null);

	const [username, setUsername] = useState('');
	const [usernameFocus, setUsernameFocus] = useState('');
	const [validUsername, setValidUsername] = useState('');

	const [password, setPassword] = useState('');
	const [passwordFocus, setPasswordFocus] = useState('');
	const [validPassword, setValidPassword] = useState('');

	const [cfrmPassword, setCfrmPassword] = useState('');
	const [cfrmPasswordFocus, setCfrmPasswordFocus] = useState('');
	const [validCfrmPassword, setValidCfrmPassword] = useState('');

	return (
		<div className='Register'>
			<h2 className='title'>Register Form</h2>
			<form className='form'>
				<input 
					className='input'
				  type='text'
					placeholder='Username'
					value={username}
					onChange={(e)=>setUsername(e.target.value)}
					onFocus={()=>setUsernameFocus(true)}
					onBlur={()=>setUsernameFocus(false)}
					aria-invalid = {validUsername}
					aria-describedby='username-note'
					ref={usernameRef}
				/>
				<input 
					className='input'
				  type='password'
					placeholder='Create Password'
					value={password}
					onChange={(e)=>setPassword(e.target.value)}
					onFocus={()=>setPasswordFocus(true)}
					onBlur={()=>setPasswordFocus(false)}
					aria-invalid = {validPassword}
					aria-describedby='password-note'
					ref={pwdRef}
				/>
				<input 
					className='input'
				  type='password'
					placeholder='Confirm Password'
					value={cfrmPassword}
					onChange={(e)=>setCfrmPassword(e.target.value)}
					onFocus={()=>setCfrmPasswordFocus(true)}
					onBlur={()=>setCfrmPasswordFocus(false)}
					aria-invalid = {validCfrmPassword}
					aria-describedby='cfrmpassword-note'
					ref={cfrmPwdRef}
				/>
				<input 
					className='input submit__btn'
				  type='submit'
					value="Register"
					ref={btnRef}
				/>
			</form>
			<p className='para__error'>error</p>
			<p className='para__footer'>Already registered? <span>Login</span></p>
		</div>
	)
}

export default Register
