import React, { useEffect, useRef, useState } from 'react';

import '../css/CreateTodo.css';

class Todo {
	constructor(title, body) {
		this.title = title;
		this.body = body;
		this.status = 'waiting';
	}
}

const CreateTodo = () => {
	const [state, setState] = useState({ title: '', titleFocus: false, body: '', bodyFocus: false });
	// const [titleFocus, setTitleFocus] = useState(false);
	const [error, setError] = useState(false);
	const titleRef = useRef(null);
	const bodyRef = useRef(null);
	const btnRef = useRef(null);

	useEffect(() => {
		titleRef.current.focus();
		// bodyRef.focus = true;
		console.log(titleRef.focus);
	}, []);

	const changeHandler = (event) => {
		setState({ ...state, [event.target.name]: event.target.value });
	};
	const submitHandler = (event) => {
		event.preventDefault();
		const newTodo = new Todo(state.title, state.body);
		//update context state

		console.log(newTodo);
	};
	const titleValidator = (event) => {
		if (event.key === 'Enter') {
			if (state.title.trim() === '') {
				titleRef.focus = true;
				setError(true);
			} else {
				bodyRef.current.focus();
				bodyRef.focus = true;
			}
		}
	};


	return (
		<div className='CreateTodo'>
			<div>
				<input
					className={titleRef.focus && error ? 'input error' : 'input'}
					id='title'
					type='text'
					name='title'
					placeholder='The title here'
					required
					title='is required'
					ref={titleRef}
					value={state.title}
					onChange={changeHandler}
					onKeyDown={titleValidator}
					onFocus={() => setState({ ...state, titleFocus: true, bodyFocus: false })}
					onBlur={() => setState({ ...state, titleFocus: false })}
				/>

			</div>

			<div>
				<textarea
					className={bodyRef.focus && error ? 'input error' : 'input'}
					id='body'
					type='text'
					name='body'
					rows={5}
					placeholder='The description goes here'
					required
					ref={bodyRef}
					value={state.body}
					onChange={changeHandler}
					onKeyDown={(event) => { if (event.key === 'Enter') btnRef.current.focus(); }}
					onFocus={() => setState({ ...state, titleFocus: false, bodyFocus: true, })}
					onBlur={() => setState({ ...state, bodyFocus: false })}
				/>
			</div>

			<div>
				<input
					className='input btn'
					id='title'
					type='button'
					ref={btnRef}
					value='Create a Todo'
					onChange={changeHandler}
					onClick={submitHandler}

				/>
			</div>
		</div>
	);
};

export default CreateTodo;
