import React from 'react';
import {Link} from 'react-router-dom';

const Login = () => {
	return (
		<div className='Login'>
			<h2 className='title'>Register Form</h2>
			<form className='form'>
				<input
					className='input'
					type='text'
					placeholder='Username'
					value={username}
					onChange={(e) => setUsername(e.target.value)}
					onFocus={() => setUsernameFocus(true)}
					onBlur={() => setUsernameFocus(false)}
					aria-invalid={validUsername ? "false" : "true"}
					aria-describedby='username-note'
					ref={usernameRef}
				/>
				<input
					className='input'
					type='password'
					placeholder='Create Password'
					value={password}
					onChange={(e) => setPassword(e.target.value)}
					onFocus={() => setPasswordFocus(true)}
					onBlur={() => setPasswordFocus(false)}
					aria-invalid={validPassword ? "false" : "true"}
					aria-describedby='password-note'
					ref={pwdRef}
				/>
				<input
					className='input'
					type='password'
					placeholder='Confirm Password'
					value={cfrmPassword}
					onChange={(e) => setCfrmPassword(e.target.value)}
					onFocus={() => setCfrmPasswordFocus(true)}
					onBlur={() => setCfrmPasswordFocus(false)}
					aria-invalid={validCfrmPassword ? "false" : "true"}
					aria-describedby='cfrmpassword-note'
					ref={cfrmPwdRef}
				/>
				<input
					className='input submit__btn'
					type='submit'
					value="Register"
					ref={btnRef}
				/>
			</form>
			<p className='para__error'>Login Error</p>
			<p className='para__footer'>Already registered?
				<Link to="/register">Register</Link>
			</p>
		</div>
	)
}

export default Login
