import React, { useEffect, useRef, useState } from 'react';

import '../css/CreateTodo.css';

class Todo {
	constructor(title, body) {
		this.title = title;
		this.body = body;
		this.status = 'waiting';
	}
}

const CreateTodo = () => {
	const [state, setState] = useState({ title: '', titleFocus: false, body: '', bodyFocus: false });
	const [error, setError] = useState(false);
	const titleRef = useRef(null);
	const bodyRef = useRef(null);
	const btnRef = useRef(null);

	useEffect(() => {
		titleRef.current.focus();
		// bodyRef.focus = true;
		console.log(titleRef.focus);
	}, []);

	const changeHandler = (event) => {
		setState({ ...state, [event.target.name]: event.target.value });
	};
	const submitHandler = (event) => {
		event.preventDefault();
		const title = state.title.trim();
		const body = state.body.trim();
		if (title && body) {
			const newTodo = new Todo(state.title, state.body);
			//update context state
			console.log(newTodo);
			return;
		}
		if (!title) {
			titleRef.focus = true;
			setError(true);
		}
		if (!body) {
			bodyRef.focus = true;
			setError(true);
		}
	};

	const inputValidator = (event) => {
		const targetName = event.target.name;
		const refName = `${event.target.name}Ref`;

		if (event.key === 'Enter') {
			if (state[targetName].trim() === '') {
				[refName].focus = true;
				setError(true);
			} else {
				bodyRef.current.focus();
				bodyRef.focus = true;
			}
		}
	};


	return (
		<div className='CreateTodo'>
			<div>
				<input
					className={titleRef.focus && error ? 'input error' : 'input'}
					id='title'
					type='text'
					name='title'
					placeholder='The title here'
					required
					title='is required'
					ref={titleRef}
					value={state.title}
					onChange={changeHandler}
					onKeyDown={inputValidator}
					onFocus={() => { setError(false); titleRef.focus = true; bodyRef.focus = false; }}
					onBlur={() => titleRef.focus = false}
				/>
			</div>

			<div>
				<textarea
					className={bodyRef.focus && error ? 'input error' : 'input'}
					id='body'
					type='text'
					name='body'
					rows={5}
					placeholder='The description goes here'
					required
					ref={bodyRef}
					value={state.body}
					onChange={changeHandler}
					onFocus={() => { setError(false); titleRef.focus = false; bodyRef.focus = true; }}
					onBlur={() => bodyRef.focus = false}
				/>
			</div>

			<div>
				<input
					className='input btn'
					id='title'
					type='button'
					ref={btnRef}
					value='Create a Todo'
					onChange={changeHandler}
					onClick={submitHandler}

				/>
			</div>
		</div>
	);
};

export default CreateTodo;
