import React from 'react';

import '../css/Todos2.css';

const Todos = ({ todos, updateTodo }) => {

	const changeHandler = (event, index) => {
		const ct = [...todos];
		ct[index].status = event.target.value;
		updateTodo(ct);
	};

	return (
		<div className='Todos--comp'>
			<div className="filter">
				<ul>
					<li>all todos</li>
					<li>waitings</li>
					<li>inprogresses</li>
					<li>completed</li>
				</ul>
			</div>
			{
				todos?.map((todo, index) => {
					return (
						<div key={index} className='todo'>
							<dl className='todo__header--con'>
								<dt className='todo__title'>{todo.title}</dt>
								<dd className='todo__body'>{todo.body}</dd>
							</dl>
							<div className='todo__status--con'>
								<div className='todo__status'>
									<input
										className='todo__status--item'
										id={'waiting' + index}
										type='radio'
										name={todo.title}
										value='waiting'
										checked={todo.status === 'waiting'}
										onChange={(e) => changeHandler(e, index)}
									/>
									<label className='todo__status--item' htmlFor={'waiting' + index} >waiting</label>
								</div>
								<div className='todo__status'>
									<input
										className='todo__status--item'
										id={'inprogress' + index}
										type='radio'
										name={todo.title}
										value='inprogress'
										checked={todo.status === 'inprogress'}
										onChange={(e) => changeHandler(e, index)}
									/>
									<label className='todo__status--item' htmlFor={'inprogress' + index}>inprogress</label>
								</div >
								<div className='todo__status'>
									<input
										className='todo__status--item'
										id={'completed' + index}
										type='radio'
										name={todo.title}
										value='completed'
										checked={todo.status === 'completed'}
										onChange={(e) => changeHandler(e, index)}
									/>
									<label className='todo__status--item' htmlFor={'completed' + index}>completed</label>
								</div>
							</div>
						</div>
					);
				})
			}
		</div>
	);
};

export default Todos;
