import React from 'react';

import '../css/Todos.css';

const Todos = ({ todos, updateTodo }) => {

	const changeHandler = (event, index) => {
		const ct = [...todos];
		ct[index].status = event.target.value;
		updateTodo(ct);
	};
	return (
		<div className='Todos'>
			{
				todos.map((todo, index) => {
					return (
						<div key={index} className='todo'>
							<dl className='todo__header--con'>
								<dt className='todo__title'>{todo.title}</dt>
								<dd className='todo__body'>{todo.body}</dd>
							</dl>
							<div className='todo__status--con'>
								<div className='todo__status'>
									<input
										className='todo__status--item'
										id='waiting'
										type='radio'
										name={todo.title}
										value='waiting'
										checked={todo.status === 'waiting'}
										onChange={() => changeHandler(index)}
									/>
									<label className='todo__status--item' htmlFor='waiting' >waiting</label>
								</div>
								<div className='todo__status'>
									<input
										className='todo__status--item'
										id='inprogress'
										type='radio'
										name={todo.title}
										value='inprogress'
										checked={todo.status === 'inprogress'}
										onChange={() => changeHandler(index)}
									/>
									<label className='todo__status--item' htmlFor='inprogress'>inprogress</label>
								</div >
								<div className='todo__status'>
									<input
										className='todo__status--item'
										id='completed'
										type='radio'
										name={todo.title}
										value='completed'
										checked={todo.status === 'completed'}
										onChange={() => changeHandler(index)}
									/>
									<label className='todo__status--item' htmlFor='completed'>completed</label>
								</div>
							</div>
						</div>
					);
				})
			}
		</div>
	);
};

export default Todos;
