import React, { useEffect, useState } from 'react';
import Todos from './components/Todos';
import Todos2 from './components/Todos2';
import CreateTodo from './components/CreateTodo';

import { todos as todosData } from './data/data';
import { filteringData } from './data/data';

import './css/App.css';
import Filter from './components/Filter';

const App = () => {
	const [originalTodos, setOriginalTodos] = useState([]);
	const [todos, setTodos] = useState([]);
	const [filterData, setFilterData] = useState(filteringData);

	useEffect(() => {
		const ntodos = JSON.parse(localStorage.getItem('todos')) || todosData;
		setTodos(ntodos);
		setOriginalTodos(ntodos)
	}, [])

	const addTodo = (todo) => {
		setTodos([todo, ...todos]);
		saveTodos([todo, ...todos]);
	};

	const updateTodo = (ntodos) => {
		saveTodos(ntodos)
		setTodos(ntodos);
	};
	const saveTodos = (ntodos) => {
		localStorage.setItem('todos', JSON.stringify(ntodos));
	};
	const filterHandler = (e, index) => {
		const nd = filterData.map(d => ({ ...d, selected: false }));
		nd[index] = { ...nd[index], selected: true };
		console.log(nd);
		if (index !== 0) {
			const ntodos = originalTodos.filter(todo => todo.status === nd[index].value);
			setTodos(ntodos);
		} else {
			setTodos(originalTodos);
		}
		setFilterData(nd);

	};

	return (
		<div className='App'>
			<Filter data={filterData} clickHandler={filterHandler} />
			<Todos todos={todos} updateTodo={updateTodo} />
			{/* <Todos2 todos={todos} updateTodo={updateTodo} /> */}
			<CreateTodo addTodo={addTodo} />
		</div>
	);
}

export default App;
