import React, { useState } from 'react';
import '../css/Filter.css';

const filteringData = [
	{ label: 'all todos', selected: true },
	{ label: 'waitings', selected: false },
	{ label: 'inprogresses', selected: false },
	{ label: 'completeds', selected: false },
];
const Filter = () => {
	const [data, setData] = useState(filteringData);
	const clickHandler = (e, index) => {
		const targetSiblings = e.target.parentNode.childNodes;
		targetSiblings.forEach(tn => tn.classList.remove('selected'));
		e.target.classList.toggle('selected');
	};
	return (
		<div className='Filter--comp'>
			{
				data.map((filter, index) => {
					return (
						<li key={index} className='filter__list--item' onClick={() => clickHandler(index)}>{filter.label}</li>
					);
				})
			}

			<li className='filter__list--item' onClick={clickHandler}>waitings</li>
			<li className='filter__list--item' onClick={clickHandler}>inprogresses</li>
			<li className='filter__list--item' onClick={clickHandler}>completed</li>


		</div>
	);
};

export default Filter;
